"use strict";function itemTemplate(e){return'\n    <li class="list-group-item list-group-item-action d-flex align-items-center justify-content-between">\n        <span class="item-text">'.concat(e.text,'</span>\n        <div>\n            <button data-id="').concat(e._id,'" class="edit-me btn btn-secondary btn-sm mr-1">Edit</button>\n            <button data-id="').concat(e._id,'" class="delete-me btn btn-danger btn-sm">Delete</button>\n        </div>\n    </li>\n    ')}var ourHTML=items.map((function(e){return itemTemplate(e)})).join("");document.getElementById("item-list").insertAdjacentHTML("beforeend",ourHTML);var createField=document.getElementById("create-field");document.getElementById("create-form").addEventListener("submit",(function(e){e.preventDefault(),createField.value&&axios.post("/create-item",{text:createField.value}).then((function(e){document.getElementById("item-list").insertAdjacentHTML("beforeend",itemTemplate(e.data)),createField.value="",createField.focus()})).catch((function(){console.log("Error!")}))})),document.addEventListener("click",(function(e){if(e.target.classList.contains("edit-me")){var t=prompt("Enter new text:",e.target.parentElement.parentElement.querySelector(".item-text").innerHTML);t&&axios.post("/update-item",{text:t,id:e.target.getAttribute("data-id")}).then((function(){e.target.parentElement.parentElement.querySelector(".item-text").innerHTML=t})).catch((function(){console.log("Error!")}))}e.target.classList.contains("delete-me")&&confirm("Are you sure?")&&axios.post("/delete-item",{id:e.target.getAttribute("data-id")}).then((function(){e.target.parentElement.parentElement.remove()})).catch((function(){console.log("Error!")}))}));